package Array;

public class FindHowManyduplicateInArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a[]= {100,24,100,545,30,100,334};
		
		int num=100;
		
		int Count=0;
		
		for(int value:a)
		{
			
			if(value==num)
			{
				Count++;
			}
				
		}
		
		
		System.out.println(Count);
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
